/*! HTML5 Boilerplate v8.0.0 | MIT License | https://html5boilerplate.com/ */

/* main.css 2.1.0 | MIT License | https://github.com/h5bp/main.css#readme */
/*
 * What follows is the result of much research on cross-browser styling.
 * Credit left inline and big thanks to Nicolas Gallagher, Jonathan Neal,
 * Kroc Camen, and the H5BP dev community and team.
 */

/* ==========================================================================
   Base styles: opinionated defaults
   ========================================================================== */

   html {
    color: #222;
    font-size: 1em;
    line-height: 1.4;
  }
  
  /*
   * Remove text-shadow in selection highlight:
   * https://twitter.com/miketaylr/status/12228805301
   *
   * Vendor-prefixed and regular ::selection selectors cannot be combined:
   * https://stackoverflow.com/a/16982510/7133471
   *
   * Customize the background color to match your design.
   */
  
  ::-moz-selection {
    background: #b3d4fc;
    text-shadow: none;
  }
  
  ::selection {
    background: #b3d4fc;
    text-shadow: none;
  }
  
  /*
   * A better looking default horizontal rule
   */
  
  hr {
    display: block;
    height: 1px;
    border: 0;
    border-top: 1px solid #ccc;
    margin: 1em 0;
    padding: 0;
  }
  
  /*
   * Remove the gap between audio, canvas, iframes,
   * images, videos and the bottom of their containers:
   * https://github.com/h5bp/html5-boilerplate/issues/440
   */
  
  audio,
  canvas,
  iframe,
  img,
  svg,
  video {
    vertical-align: middle;
  }
  
  /*
   * Remove default fieldset styles.
   */
  
  fieldset {
    border: 0;
    margin: 0;
    padding: 0;
  }
  
  /*
   * Allow only vertical resizing of textareas.
   */
  
  textarea {
    resize: vertical;
  }
  
  /* ==========================================================================
     Author's custom styles
     ========================================================================== */
  
  /***** GENERAL STYLES *****/
  
  html { 
    font-size: 16px;
  }
  
  body { 
    color: #e2e3ed;
    font-family: 'Open Sans', sans-serif;
    font-size: 1em; 
    text-align: left;
  /*! text-align:left because of the flexible nature of the divs? */
  }
  
  ul,
  ol { 
    list-style-type: none;
  }
  
  a { 
    color: #e2e3ed;
    margin: 0 20px;
    text-decoration: none;
  }
  
  h2 { 
    font-family: 'Poppins', sans-serif;
    font-size: 2em;
    font-weight: 500;
  }
  
  h3, 
  h4 {
    font-family: 'Poppins', sans-serif;
    font-size: 1.5em;
    font-weight: 500;
    margin: 20px 0;
  }
  
  p { 
    font-size: .9em;
    line-height: 1.25em;
  }
  
  img { 
    width: 100%;
  }
  
  figure { 
    display: flex;
    margin: 0;
  }
  
  address { 
    font-style: normal;
  }
  
  .content-wrapper {
    width: 90%;
    margin: 50px 0;
  }
  /***** BASE HEADER - FOOTER STYLES *****/
  
  header, 
  footer {
    display: flex;
    align-items: center;
    background-color: #202322;
    flex-flow: row wrap;
    justify-content: center;
  }
  
  header { 
    padding: 15px 0;
  }
  
  header .content-wrapper { 
    display: flex;
    align-items: center;
    flex-flow: row wrap;
    justify-content: center; 
    margin: 0;
  }
  
 /* TRANSFERED HEADER STYLES TO NavBar.css*/
  
 
  
  footer {
    flex-direction: column;
    text-align: center;
  }
  
  footer .contact { 
    margin-bottom: 50px;
  }
  
  footer content { 
    margin-bottom: 50px;
  }
  
  footer .content-wrapper {
    margin: 50px 0;
    width: 55%;
  }
  
  footer .copywright { 
    margin: 20px 0;
  }
  
  footer p {
    margin: 0;
  }
  
  footer .social-icons{ 
    display: flex;
    align-items: center;
    justify-content: space-around;
  }
  
  footer .social-icons p {
    display: flex;
    align-items: center;
    background-color: #527158;
    border-radius: 80%;
    /*! specifics of border-radius property?*/
    height: 40px;
    justify-content: center;
    width: 40px;
  }
  
  footer .social-icons p i {
    font-size: 1.3125em;
  }
  
  
  /***** MOBILE BODY STYLES *****/ 
  
  /*========== Homepage ==========*/
  
  /* HERO */
  .hero-area {
    display: flex;
    background: url('../img/jake-melara-30679-unsplash.jpg') center no-repeat;
    background-size: cover;
    flex-flow: row wrap;
    justify-content: center; 
    min-height: 500px;
  }
  
  .hero-area h1 { 
    font-family: 'Poppins', sans-serif;
    font-size: 2.25em; 
    font-weight: 500;
    line-height: 1.2em;
  }
  
  /* ASIDE */
  aside {
    display: flex;
    align-items: center;
    background-color: #527158;
    flex-flow: row wrap;
    justify-content: center;
  }
  
  aside .sign-up-form { 
    margin-top: 30px;
  }
  
  aside .sign-up-form-description h3 { 
    padding-top: 10px;
  }
  
  input[type=text] { 
    box-sizing: border-box;
    color: #2a423a;
    font-style: italic;
    padding: 1em;
    width: 100%;
  }
  
  input[type=button] {
    background-color: #2a423a;
    border: none;
    color: #e2e3ed;
    font-family: 'Poppins', Helvetica, Arial, sans-serif; 
    font-size: 1.125em;
    font-weight: 500;
    margin-top: 20px;
    padding: 20px 0;
    text-align: center;
    text-transform: uppercase;
    width: 100%;
  }
  
  /* FEATURES */
  .features {
    display: flex;
    background: url('../img/rawpixel-800820-unsplash1.jpg') center no-repeat;
    background-size: cover;
    flex-flow: row wrap;
    justify-content: center;
  }
  
  
  .features article { 
    width: 63%;
    margin: 30px 0;
  }
  
  .features .feature-items {
    display: flex;
    align-items: center;
    flex-flow: row wrap;
    justify-content: center; 
  }
  
  .features h2 {
    font-size: 2em;
    margin-bottom: 40px;
  }
  
  .features i { 
    font-size: 3.75em;
    margin-bottom: 40px;
  }
  
  .features p {
    margin: 0; 
    text-align: center;
  }
  
  /* REVIEWS */
  .reviews {
    display: flex;
    background-color: #2a423a;
    flex-flow: row wrap;
    justify-content: center; 
  }
  
  .reviews h2 { 
    font-size: 2.2em;
    line-height: 1.25em;
    margin-bottom: 30px;
    padding-left: 5px; 
  }
  
  .reviews h3 { 
    font-size: 1.8em;
    line-height: 1.5em;
    padding: 20px 0;
    padding-left: 15px;
    width: 60%; 
  }
  
  .reviews p {
    font-size: 1em;
    padding-left: 15px;
  }
  
  /*========== About Page ==========*/
  /* MISSION */
  .mission {
    display: flex;
    align-items: center;
    background-color: #527158;
    flex-flow: row wrap;
    justify-content: center;
  }
  
  .mission h1{ 
    font-family: 'Poppins', sans-serif;
    font-size: 2.25em; 
    font-weight: 500;
    line-height: 1.2em;
  }
  
  /* MAP */
  .map figure {
    height: 0;
    overflow: hidden;
    padding-bottom: 75%;
    position: relative;
    max-width: 100%;
  }
  
  .map iframe { 
    height: 100%;
    left: 0;
    position: absolute;
    top: 0;
    width: 100%;
  }
  
  /* CONTACT */
  .contact-section {
    display: flex;
    align-items: center;
    background-color: #2a423a;
    flex-flow: row wrap;
    justify-content: center;
  }
  
  .contact-content p { 
    margin: 0;
  }
  
  /*========== FAQ Page ==========*/
  /* FAQ */
  .faq {
    display: flex;
    align-items: center;
    background-color: #527158;
    flex-flow: row wrap;
    justify-content: center;
  }
  
  .faq .content-wrapper {
    display: flex;
    align-items: center;
    flex-flow: row wrap;
    justify-content: center; 
    margin: 50px 0;
  }
  
  .faq h2 { 
    line-height: 1em;
    margin-bottom: 40px;
  }
  
  .faq content { 
    margin-bottom: 50px;
  }
  
  .faq h4 { 
    line-height: 1.333em;
  }
  
  /* ==========================================================================
     Helper classes
     ========================================================================== */
  
  /*
   * Hide visually and from screen readers
   */
  
  .hidden,
  [hidden] {
    display: none !important;
  }
  
  /*
   * Hide only visually, but have it available for screen readers:
   * https://snook.ca/archives/html_and_css/hiding-content-for-accessibility
   *
   * 1. For long content, line feeds are not interpreted as spaces and small width
   *    causes content to wrap 1 word per line:
   *    https://medium.com/@jessebeach/beware-smushed-off-screen-accessible-text-5952a4c2cbfe
   */
  
  .sr-only {
    border: 0;
    clip: rect(0, 0, 0, 0);
    height: 1px;
    margin: -1px;
    overflow: hidden;
    padding: 0;
    position: absolute;
    white-space: nowrap;
    width: 1px;
    /* 1 */
  }
  
  /*
   * Extends the .sr-only class to allow the element
   * to be focusable when navigated to via the keyboard:
   * https://www.drupal.org/node/897638
   */
  
  .sr-only.focusable:active,
  .sr-only.focusable:focus {
    clip: auto;
    height: auto;
    margin: 0;
    overflow: visible;
    position: static;
    white-space: inherit;
    width: auto;
  }
  
  /*
   * Hide visually and from screen readers, but maintain layout
   */
  
  .invisible {
    visibility: hidden;
  }
  
  /*
   * Clearfix: contain floats
   *
   * For modern browsers
   * 1. The space content is one way to avoid an Opera bug when the
   *    `contenteditable` attribute is included anywhere else in the document.
   *    Otherwise it causes space to appear at the top and bottom of elements
   *    that receive the `clearfix` class.
   * 2. The use of `table` rather than `block` is only necessary if using
   *    `:before` to contain the top-margins of child elements.
   */
  
  .clearfix::before,
  .clearfix::after {
    content: " ";
    display: table;
  }
  
  .clearfix::after {
    clear: both;
  }
  
